import React from 'react';
import {
  useState,
  useEffect,
  useRef,
  useImperativeHandle,
  forwardRef,
} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import Divider from '@material-ui/core/Divider';
import Grid from '@material-ui/core/Grid';
import DisplayTable from './DisplayTable';
import Form from './Form';
import Functions from './Functions';
import { Button, Box } from '@material-ui/core';
// import Table from './';

const useStyles = makeStyles((theme) => ({
  try: {
    // height: 100%,
    // height: 100% ,
    background: 'linear-gradient(315deg, #63d471 0%, #233329 74%)',
  },
  container: {
    display: 'grid',
    gridTemplateColumns: 'repeat(12, 1fr)',
    gridGap: theme.spacing(3),
  },
  paper: {
    width: '900',
    height: '500px',
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
    whiteSpace: 'nowrap',
    marginBottom: theme.spacing(50),
  },
  divider: {
    margin: theme.spacing(2, 0),
  },
}));

const Display = forwardRef((props, ref) => {
  const classes = useStyles();
  const [updateArr, setUpdateArr] = useState([]);
  const [trans, setTrnas] = useState([]);
  const data = props.transactionBody;
  console.log('this is data:');
  console.log(data);

  const [val, setVal] = useState(0);
  let value = 0;
  const updateFather = (update) => {
    console.log('you arr work' + update);
  };

  useImperativeHandle(ref, () => ({
    trying() {
      console.log('hi try');
      console.log(props.transactionBody);
      setUpdateArr(props.transactionBody);
    },
    sagie() {
      console.log('hi sagie');
    },
  }));

  const clicki = () => {
    console.log(props.transactionBody);
    // setVal(2);
    console.log(props.transactionBody[0]);
    const arr = ['a', 'b', 'c'];
    // props.change(arr);
    // props.transactionBody.map(x => {
    //   console.log(x.type);
    //   setTrnas(x.bank);
    // });
  };

  useEffect(() => {
    // setTrnas(data);
    console.log('hi i am a user effect in display');
    setUpdateArr(props.transactionBody);
    props.change(updateArr);
  }, [props.transactionBody]);

  //   useEffect(() => {
  //     console.log('hi i am a user effect2');
  //     console.log(props.transactionBody);
  //   }, [val]);

  return (
    <Grid container>
      <Grid item xs={1}>
        <Paper className={classes.paper}>updates</Paper>
      </Grid>
      <Grid item xs={10}>
        <DisplayTable trans={props.transactionBody} />
        <Paper className={classes.paper}>
          what you add is here
          <button
            type='button'
            // onClick={e => {
            //   click(e);
            //   return false;
            // }}
            onClick={clicki}
          >
            clicki
          </button>
          <h1>
            here: {trans}
            {props.transactionBody.map((item) => (
              <h3>{item.type}</h3>
            ))}
            {updateArr}
          </h1>
        </Paper>
      </Grid>
      <Grid item xs={1}>
        <Paper className={classes.paper}>updates</Paper>
      </Grid>
    </Grid>
  );
});

export default Display;
