import React from 'react';
import { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import Divider from '@material-ui/core/Divider';
import Grid from '@material-ui/core/Grid';
import DisplayTable from './DisplayTable';
import Form from './Form';
import Functions from './Functions';
import { Button, Box } from '@material-ui/core';
// import Table from './';

const useStyles = makeStyles(theme => ({
  try: {
    // height: 100%,
    // height: 100% ,
    background: 'linear-gradient(315deg, #63d471 0%, #233329 74%)'
  },
  container: {
    display: 'grid',
    gridTemplateColumns: 'repeat(12, 1fr)',
    gridGap: theme.spacing(3)
  },
  paper: {
    width: '900',
    height: '500px',
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
    whiteSpace: 'nowrap',
    marginBottom: theme.spacing(50)
  },
  divider: {
    margin: theme.spacing(2, 0)
  }
}));

function Display(props) {
  const classes = useStyles();
  const [trans, setTrnas] = useState(null);
  const data = props.transactionBody2;
  setTrnas(data);

  const [val, setVal] = useState(0);
  let value = 0;
  const clicki = () => {
    console.log(props.transactionBody2);
    setVal(1);
    setTrnas(data);
    console.log(props.transactionBody2[0]);
    // props.transactionBody2.map(x => {
    //   console.log(x.type);
    //   setTrnas(x.bank);
    // });
  };

  useEffect(() => {
    console.log('hi i am a user effect');
    console.log(trans);
  }, [trans]);

  //   useEffect(() => {
  //     console.log('hi i am a user effect2');
  //     console.log(props.transactionBody2);
  //   }, [val]);

  return (
    <Grid container>
      <Grid item xs={1}>
        <Paper className={classes.paper}>updates</Paper>
      </Grid>
      <Grid item xs={10}>
        <DisplayTable trans={props.transactionBody2} />
        <Paper className={classes.paper}>
          what you add is here
          <button
            type='button'
            // onClick={e => {
            //   click(e);
            //   return false;
            // }}
            onClick={clicki}
          >
            clicki
          </button>
          <h1>
            here: {trans}
            {props.transactionBody2.map(item => (
              <h3>{item.type}</h3>
            ))}
          </h1>
        </Paper>
      </Grid>
      <Grid item xs={1}>
        <Paper className={classes.paper}>updates</Paper>
      </Grid>
    </Grid>
  );
}

export default Display;
