import React from 'react';
import MaterialTable from 'material-table';
const db = require('./DB');

// import columns from './DB';

export default function MaterialTableDemo() {
  var dataDb = [];
  const [state, setState] = React.useState({
    columns: db.columns,
    data: dataDb,
  });
  let requestOptions = {
    method: 'GET',
    headers: { 'Content-Type': 'application/json' },
  };
  fetch('http://localhost:8000/getData', requestOptions)
    .then((res) => {
      return res.json();
    })
    .then((data) => {
      // then print response status
      // data = response.json();
      console.log('this is the data in fetch:');
      console.log(data.result);
      dataDb = data.result;
      setState((prevState) => {
        return { ...prevState, dataDb };
      });
    });

  return (
    <MaterialTable
      title='Transactions'
      columns={state.columns}
      data={state.data}
      editable={{
        onRowAdd: (newData) =>
          new Promise((resolve) => {
            setTimeout(() => {
              resolve();
              setState((prevState) => {
                const data = [...prevState.data];
                data.push(newData);
                return { ...prevState, data };
              });
            }, 600);
          }),
        onRowUpdate: (newData, oldData) =>
          new Promise((resolve) => {
            setTimeout(() => {
              resolve();
              if (oldData) {
                setState((prevState) => {
                  const data = [...prevState.data];
                  data[data.indexOf(oldData)] = newData;
                  return { ...prevState, data };
                });
              }
            }, 600);
          }),
        onRowDelete: (oldData) =>
          new Promise((resolve) => {
            setTimeout(() => {
              resolve();
              setState((prevState) => {
                const data = [...prevState.data];
                data.splice(data.indexOf(oldData), 1);
                return { ...prevState, data };
              });
            }, 600);
          }),
      }}
    />
  );
}
