import React from 'react';
import { useEffect } from 'react';
import MaterialTable from 'material-table';

const db = require('./DB');

export default function MaterialTableDemo(props) {
  var dataDb = [];
  const [state, setState] = React.useState({
    columns: db.columns,
    data: dataDb,
  });
  let requestOptions = {
    method: 'GET',
    headers: { 'Content-Type': 'application/json' },
  };
  useEffect(() => {
    fetch('http://localhost:8000/getData', requestOptions)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        dataDb = data.result;
        setState({ columns: db.columns, data: dataDb });
      });
  }, []);

  useEffect(() => {
    console.log('hi i am in useafeect-table you change the props?');
    var arr = [...state.data];
    arr.push(...props.updateArr);
    setState({ columns: db.columns, data: arr });
  }, [props.updateArr]);

  return (
    <MaterialTable
      style={{
        width: '900',
        // minHeight: '516px',
        maxHeight: '516px',
      }}
      title='Transactions'
      columns={state.columns}
      data={state.data}
      options={{
        rowStyle: (rowData) => {
          if (rowData.type === 'Add') {
            return {
              // background: 'linear-gradient(315deg, #ddffef 0%, #7bed9f 74%)',
              background:
                ' linear-gradient(90deg, rgba(190,255,224,1) 31%, rgba(250,255,253,0.7987570028011204) 100%)',
            };
          }
          if (rowData.type === 'Minus') {
            return {
              background:
                'linear-gradient(90deg, rgba(255,190,190,1) 31%, rgba(250,255,253,0.7987570028011204) 100%)',
              // 'linear-gradient( 89.5deg,  rgba(246,114,128,1) 0.2%, rgba(248,177,149,1) 90.6% )',
            };
          }

          return {};
        },
      }}
      editable={{
        onRowAdd: (newData) =>
          new Promise((resolve) => {
            setTimeout(() => {
              resolve();
              setState((prevState) => {
                const data = [...prevState.data];
                data.push(newData);
                return { ...prevState, data };
              });
            }, 600);
          }),
        onRowUpdate: (newData, oldData) =>
          new Promise((resolve) => {
            setTimeout(() => {
              resolve();
              if (oldData) {
                setState((prevState) => {
                  const data = [...prevState.data];
                  data[data.indexOf(oldData)] = newData;
                  return { ...prevState, data };
                });
              }
            }, 600);
          }),
        onRowDelete: (oldData) =>
          new Promise((resolve) => {
            setTimeout(() => {
              resolve();
              setState((prevState) => {
                const data = [...prevState.data];
                data.splice(data.indexOf(oldData), 1);
                return { ...prevState, data };
              });
            }, 600);
          }),
      }}
    />
  );
}
